mapper.select:
select id,title,create_date from sys_news


        news_category_id = #{newsCategoryId}


order by create_date desc

select sn.id,sn.code,sn.title,sn.content,snc.name as news_category_name,su.actual_name create_name,sn.create_date
from sys_news as sn,sys_news_category as snc,sys_user as su
where sn.news_category_id = snc.id and sn.create_id = su.id and sn.id = #{newsId}
