mapper.select:
select cree.id,concat(cb.no,'-',cbu.no,'-',cbue.room_number) as room_name,cree.building_unit_estate_id as estate_id,cree.type,ur.name,ur.id_type,ur.id_number,cree.effective_time_start,cree.effective_time_end,cree.status
from cpm_resident_estate_examine as cree,user_resident as ur,cpm_building_unit_estate as cbue,cpm_building_unit as cbu,cpm_building as cb
where cree.resident_id = ur.id and cree.building_unit_estate_id = cbue.id and cbue.building_unit_id = cbu.id and cbu.building_id = cb.id and cree.is_delete = 1 and cree.id = #{estateExamineId}
