mapper.select:
select b.id,b.building_name,b.building_unit_no,b.room_number,b.status,b.type,b.construction_area,b.indoor_area,group_concat(b.user_resident_name) as user_resident_name,group_concat(b.user_resident_tel) as user_resident_tel
from (
select a.id,a.building_name,a.building_unit_no,a.room_number,a.status,a.type,a.construction_area,a.indoor_area,ur.name as user_resident_name,ur.tel as user_resident_tel
from (
select cbue.*, cbu.id as cbu_id, cbu.no as building_unit_no, cb.name as building_name,cre.resident_id as resident_id
from cpm_building_unit_estate cbue,cpm_building cb,cpm_building_unit cbu,cpm_resident_estate cre
where cbue.building_unit_id = cbu.id and cbu.building_id = cb.id and cbue.id = cre.building_unit_estate_id
) as a LEFT JOIN user_resident ur
on a.resident_id = ur.id
) as b


        and b.building_name like concat('%',#{buildingName},'%')


        and b.building_unit_no like concat('%',#{buildingUnitNo},'%')


        and b.room_number like concat('%',#{roomNumber},'%')


        and b.status = #{status}


        and b.user_resident_name like concat('%',#{userResidentName},'%')


        and b.user_resident_tel = #{userResidentTel}


GROUP BY b.id

select id as value,room_number as label
from cpm_building_unit_estate
where is_delete =1 and building_unit_id = #{id}
