mapper.select:
select uao.id,uao.status,concat(cb.no,"幢-",cbu.no,"单元",cbue.room_number) as room_name,ur.name as applicant_name,ur.type as identity,uao.name as article_out_name,uao.expected_time from user_article_out as uao,cpm_building_unit_estate as cbue,cpm_building_unit as cbu,cpm_building as cb,user_resident as ur
uao.building_unit_estate_id = cbue.id and cbue.building_unit_id = cbu.id and cbu.building_id = cb.id and ur.id = uao.applicant_id
and uao.status = #{articleOutStatus}

mapper.update:
update user_article_out

    actual_time = #{actualTime},
    export = #{export},
    status = #{status},
    review_id = #{reviewId},
    review_date = #{reviewDate}

where id = #{id}

update user_article_out

    status = #{status},
    remarks = #{remarks},
    review_id = #{reviewId},
    review_date = #{reviewDate}

where id = #{id}
