mapper.select:
select a.*
from (select sg.*,(
case sg.user_type
when 1 then (select ur.name from user_resident as ur where ur.id = sg.create_id)
when 2 then (select udp.name from user_decoration_personnel as udp where udp.id = sg.create_id)
when 3 then (select su.actual_name from sys_user as su where su.id = sg.create_id) 	end
) as create_name
from sys_gambit as sg) as a


        and a.title like concat('%',#{title},'%')


        and a.create_name like concat('%',#{createName},'%')


        and a.status = #{status}


        and a.summary like concat('%',#{summary},'%')


        and a.create_date > #{creatBeginDate}


        and a.end_date < #{creatEndDate}

    and is_delete = 1

select count(distinct a.user_type,a.create_id) as count_gambit_num
from (
select user_type,create_id from sys_gambit_theme where gambit_id = #{id}
union all
select user_type,create_id from sys_gambit_theme_comment where gambit_id = #{id}
union all
select user_type,create_id from sys_gambit_theme_like where gambit_id = #{id}) as a
