mapper.select:
select sa.id,sa.content,sa.type,ur.name as release_name,sa.create_date as release_date,sa.score
from sys_advice as sa,user_resident as ur

    sa.create_id = ur.id

        and sa.create_date  >=  #{releaseDateStart}


        and sa.create_date  <=  #{releaseDateEnd}


        and sa.score = #{score}


        and sa.type = #{type}


        ur.name like concat('%',#{releaseName},'%')
