mapper.select:
select sa.id,sa.content,sa.type,sa.status,ur.name as release_name,sa.create_date as release_date,sa.score
from sys_advice as sa,user_resident as ur

    sa.create_id = ur.id
    and sa.is_delete = 1

        and sa.status = #{status}


        and sa.create_date  >=  #{releaseDateStart}


        and sa.create_date  <=  #{releaseDateEnd}


        and sa.score = #{score}


        and sa.type = #{type}


        and (sa.type = 1 or sa.type = 2)


        ur.name like concat('%',#{releaseName},'%')

mapper.update:
update sys_advice
set is_delete = 0
where id = #{id}
