mapper.select:
select cree.id,cree.name,cree.type,ur1.tel,cree.id_type,cree.id_number,concat(cb.no,'-',cbu.no,'-',cbue.room_number) as room_name, cree.effective_time_start,cree.effective_time_end,T.resident_name,T.resident_tel,cree.create_date
from user_resident as ur1,cpm_building_unit_estate as cbue,cpm_building_unit as cbu,cpm_building as cb, cpm_resident_estate_examine as cree LEFT JOIN ( select cre.building_unit_estate_id,GROUP_CONCAT(ur2.name) as resident_name,GROUP_CONCAT(ur2.tel) as resident_tel from cpm_resident_estate as cre,user_resident as ur2 where cre.resident_id = ur2.id GROUP BY cre.building_unit_estate_id ) as T on cree.building_unit_estate_id = T.building_unit_estate_id
cree.resident_id = ur1.id and cree.building_unit_estate_id = cbue.id and cbue.building_unit_id = cbu.id and cbu.building_id = cb.id and cree.id = #{estateExamineId}
