mapper.select:
select sfc.id,sfc.code,sfc.name,su.actual_name as create_name,sfc.num,sfc.create_date
from sys_facilities_category as sfc,sys_user as su

    sfc.create_id = su.id
    and sfc.is_delete = 1

        and sfc.name like concat('%',#{name},'%')


        and sfc.code = #{code}


        and sfc.create_date  >=  #{createStartDate}


        and sfc.create_date  <=  #{createEndDate}

mapper.update:
update sys_facilities_category
set name = #{name},code = #{code}
where id = #{id}

update sys_facilities_category
set is_delete = 0
where id = #{id}
