mapper.update:
update sys_facilities_plan
set is_delete = 0
where id = #{id}

update sys_facilities_plan
set status = 2
where id = #{id}

update sys_facilities_plan
set status = 1
where id = #{id}

mapper.select:
select * from sys_facilities_execute
where facilities_plan_id = #{id}
order by sort desc limit 1
