mapper.select:
select id,code,name,type,create_date from sys_inspection_point


        and code = #{code}


        and name = #{name}

    and is_delete = 1

mapper.insert:
insert into sys_inspection_check_items(
    inspection_point_id,
    name,
    type
)values (
    #{inspectionPointId},
    #{name},
    #{type}
)
