mapper.select:
select id,code,name,status,space_time,create_date
from sys_inspection_route

    is_delete = 1

        and code = #{code}


        and name = #{name}

mapper.update:
update sys_inspection_route
name = #{name},
describes = #{describes},
space_time = #{spaceTime},
modify_id = #{modifyId}, modify_date = #{modifyDate}
where id = #{id}

mapper.select:
select sum(case sip.status when 1 then 1 else 0 end) as sum
from sys_inspection_plan as sip,sys_inspection_route as sir
where sip.inspection_route_id = sir.id and sir.id = #{id}
