mapper.select:
select sdl.id,srr.code,sdl.status,concat_ws('-',cb.no,cbu.no,cbue.room_number) as room_name,ur.name as repairman_name,ur.tel as repair_tel,srr.repair_date as repair_date,a.*,srr.froms as froms_name from sys_report_repair as srr,cpm_building_unit_estate as cbue,cpm_building_unit as cbu,cpm_building as cb,user_resident as ur,sys_dispatch_list as sdl left join ( select sdld.dispatch_list_id,su1.actual_name as distributor_name,su2.actual_name as operator_name,swotl.name as work_order_time_limit,su1.id as operator,su2.id as distributor from sys_dispatch_list_detail as sdld,sys_user as su1,sys_user as su2,sys_work_order_time_limit as swotl where su1.id = sdld.operator and su2.id = sdld.create_id and swotl.id = sdld.work_order_time_limit ) as a on sdl.id = a.dispatch_list_id
sdl.id = srr.dispatch_list_id and srr.building_unit_estate_id = cbue.id and cbue.building_unit_id = cbu.id and cbu.building_id = cb.id and ur.id = srr.repairman and sdl.sys_delete = 1 and sdl.work_order_type = 1
and srr.code like concat('%',#{code},'%')
and ur.name like concat('%',#{repairman},'%')
and cbue.room_number like concat('%',#{roomNumber},'%')
and cbu.no like concat('%',#{unitNo},'%')
and cb.no like concat('%',#{estateNo},'%')
and srr.froms = #{froms}
and a.distributor = #{distributor}
and a.operator = #{operator}
and srr.repair_date > #{repairDateStart}
and srr.repair_date < #{repairDateEnd}
and sdl.status = #{status}

select srr.*,ur.name as repair_name,su.actual_name as create_name
from sys_report_repair as srr,user_resident as ur,sys_user as su
where srr.repairman = ur.id and srr.create_id = su.id and srr.id = #{id}
