mapper.select:
select a.*,group_concat(sdpo.name SEPARATOR '\r\n') as pay_name,group_concat(sdpo.tel SEPARATOR '\r\n') as pay_tel,group_concat(sdpo.code SEPARATOR '\r\n') as order_code,group_concat(sdpo.create_date SEPARATOR '\r\n') as pay_date ,group_concat( case sdpo.remake is null when true then '暂无备注' ELSE sdpo.remake end SEPARATOR '\r\n' ) as remake from ( select sdp.*,sctd.name,cbue.room_number,cbu.no as unit_no,cb.no as estate_no,concat_ws('-',cb.no,cbu.no,cbue.room_number) as room_name,su.actual_name as create_name, (case sdp.modify_date is null when true then sdp.create_date else sdp.modify_date end) as update_date from sys_daily_payment as sdp,cpm_building_unit_estate as cbue,cpm_building_unit as cbu,cpm_building as cb,sys_user as su,sys_charges_template_detail as sctd where sdp.building_unit_estate_id = cbue.id and cbue.building_unit_id = cbu.id and cbu.building_id = cb.id and su.id =sdp.create_id and sctd.id = sdp.charges_template_detail_id and sdp.is_delete = 1 ) as a LEFT JOIN sys_daily_payment_order as sdpo on a.id = sdpo.daily_payment_id
and a.charges_template_detail_id = #{chargesTemplateDetailId}
and a.begin_date > #{beginDate}
and a.end_date < #{endDate}
and sdpo.name like concat('%',#{payName},'%')
and sdpo.tel like concat('%',#{tel},'%')
and a.room_number like concat('%',#{roomNumber},'%')
and a.unit_no like concat('%',#{unitNo},'%')
and a.estate_no like concat('%',#{estateNo},'%')
and a.status = #{status}
group by id
