mapper.select:
select salr.id,salr.reason,salr.status,salr.type,salr.start_date,salr.end_date, su1.actual_name as create_name,su1.tel as create_tel,salr.create_date, su2.actual_name as reviewer_name,salr.reviewer_date from sys_user as su1,sys_attendance_leave_record as salr LEFT JOIN sys_user as su2 on salr.reviewer = su2.id
salr.create_id = su1.id
and salr.status = #{status}
and salr.type = #{type}
and su1.tel like concat('%',#{createTel},'%')
order by salr.create_date desc

mapper.update:
update sys_attendance_record
set card_replacement_date = #{cardReplacementDate},
operator = #{operator}
where id = #{id}

update sys_attendance_leave_record
set status = #{status},
reviewer = #{reviewer},
reviewer_date = #{reviewerDate}
where id = #{id}
