mapper.update:
update alipay_order
notity_time = #{notityTime}, gmt_create = #{gmtCreate}, gmt_payment = #{gmtPayment}, gmt_refund = #{gmtRefund}, gmt_close = #{gmtClose}, trade_no = #{tradeNo}, out_biz_no = #{outBizNo}, buyer_logon_id = #{buyerLogonId}, seller_id = #{sellerId}, seller_email = #{sellerEmail}, total_amount = #{totalAmount}, receipt_amount = #{receiptAmount}, invoice_amount = #{invoiceAmount}, buyer_pay_amount = #{buyerPayAmount}
where id = #{id}

update sys_advance_payment_order
set status = #{status}
where code = #{code}

