mapper.select:
select daily_activity_num,create_date from sys_daily_activity_log


        and create_date  >=  #{beginDate}


        and create_date  <=  #{endDate}

mapper.insert:
insert into sys_daily_activity_log(
    daily_activity_num,
    create_date
) values (
    #{dailyActivityNum},
    #{createDate}
)
